import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNotification } from '../contexts/NotificationContext';
import { applicantAPI, applicantStatuses } from '../services/applicantAPI';
import ApplicantCard from './ApplicantCard';
import ApplicantDetailModal from './ApplicantDetailModal';
import BulkActionsModal from './BulkActionsModal';
import Header from './Header';
import PartGOFooter from './PartGOFooter ';
import './ApplicantDashboard.css';

const ApplicantDashboard = ({ jobId, jobTitle, onBack }) => {
    const { user } = useAuth();
    const { success, error: showError, warning } = useNotification();
    
    const [applicants, setApplicants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedApplicants, setSelectedApplicants] = useState([]);
    const [selectedApplicant, setSelectedApplicant] = useState(null);
    const [showDetailModal, setShowDetailModal] = useState(false);
    const [showBulkActions, setShowBulkActions] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const [sortBy, setSortBy] = useState('appliedAt');
    const [sortOrder, setSortOrder] = useState('desc');
    const [stats, setStats] = useState({
        totalApplicants: 0,
        avgRating: 0,
        statusBreakdown: []
    });

    useEffect(() => {
        if (jobId) {
            loadApplicants();
            loadStats();
        }
    }, [jobId, searchQuery, sortBy, sortOrder]);

    const loadApplicants = async () => {
        try {
            setLoading(true);
            const params = {
                search: searchQuery,
                sortBy,
                sortOrder
            };
            
            const response = await applicantAPI.getJobApplicants(jobId, params);
            setApplicants(response.applicants || []);
        } catch (error) {
            showError('L·ªói khi t·∫£i danh s√°ch ·ª©ng vi√™n: ' + (error.message || 'Unknown error'), 'L·ªói');
        } finally {
            setLoading(false);
        }
    };

    const loadStats = async () => {
        try {
            const response = await applicantAPI.getApplicantStats(jobId);
            setStats(response);
        } catch (error) {
            console.error('Error loading stats:', error);
        }
    };

    const handleStatusChange = async (applicantId, newStatus, note = null) => {
        try {
            await applicantAPI.updateApplicantStatus(applicantId, newStatus, note);
            success('ƒê√£ c·∫≠p nh·∫≠t tr·∫°ng th√°i ·ª©ng vi√™n', 'Th√†nh c√¥ng');
            loadApplicants();
            loadStats();
        } catch (error) {
            showError('L·ªói khi c·∫≠p nh·∫≠t tr·∫°ng th√°i: ' + (error.message || 'Unknown error'), 'L·ªói');
        }
    };

    const handleBulkStatusChange = async (status, note = null) => {
        try {
            await applicantAPI.bulkUpdateStatus(selectedApplicants, status, note);
            success(`ƒê√£ c·∫≠p nh·∫≠t tr·∫°ng th√°i cho ${selectedApplicants.length} ·ª©ng vi√™n`, 'Th√†nh c√¥ng');
            setSelectedApplicants([]);
            setShowBulkActions(false);
            loadApplicants();
            loadStats();
        } catch (error) {
            showError('L·ªói khi c·∫≠p nh·∫≠t h√†ng lo·∫°t: ' + (error.message || 'Unknown error'), 'L·ªói');
        }
    };

    const handleApplicantSelect = (applicantId) => {
        setSelectedApplicants(prev => 
            prev.includes(applicantId) 
                ? prev.filter(id => id !== applicantId)
                : [...prev, applicantId]
        );
    };

    const handleSelectAll = () => {
        if (selectedApplicants.length === applicants.length) {
            setSelectedApplicants([]);
        } else {
            setSelectedApplicants(applicants.map(a => a._id));
        }
    };

    const handleViewApplicant = (applicant) => {
        setSelectedApplicant(applicant);
        setShowDetailModal(true);
    };

    const groupApplicantsByStatus = () => {
        const grouped = {};
        applicantStatuses.forEach(status => {
            grouped[status.value] = [];
        });

        applicants.forEach(applicant => {
            if (grouped[applicant.status]) {
                grouped[applicant.status].push(applicant);
            }
        });

        return grouped;
    };

    const getStatusCount = (status) => {
        return applicants.filter(a => a.status === status).length;
    };

    const groupedApplicants = groupApplicantsByStatus();

    if (loading) {
        return (
            <div className="applicant-dashboard">
                <Header />
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>ƒêang t·∫£i danh s√°ch ·ª©ng vi√™n...</p>
                </div>
                <PartGOFooter />
            </div>
        );
    }

    return (
        <div className="applicant-dashboard">
            <Header />
            
            <div className="dashboard-container">
                {/* Header */}
                <div className="dashboard-header">
                    <div className="header-content">
                        <div className="header-top">
                            <button 
                                className="back-btn"
                                onClick={onBack}
                                title="Quay l·∫°i danh s√°ch c√¥ng vi·ªác"
                            >
                                ‚Üê Quay l·∫°i
                            </button>
                        </div>
                        <h1>Qu·∫£n l√Ω ·ª©ng vi√™n</h1>
                        <p>{jobTitle}</p>
                    </div>
                    
                    <div className="header-actions">
                        <div className="search-box">
                            <input
                                type="text"
                                placeholder="T√¨m ki·∫øm ·ª©ng vi√™n..."
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                            />
                            <span className="search-icon">üîç</span>
                        </div>
                        
                        <div className="sort-controls">
                            <select 
                                value={sortBy} 
                                onChange={(e) => setSortBy(e.target.value)}
                            >
                                <option value="appliedAt">Ng√†y n·ªôp</option>
                                <option value="rating">ƒê√°nh gi√°</option>
                                <option value="personalInfo.name">T√™n</option>
                            </select>
                            
                            <button 
                                onClick={() => setSortOrder(sortOrder === 'desc' ? 'asc' : 'desc')}
                                className="sort-btn"
                            >
                                {sortOrder === 'desc' ? '‚Üì' : '‚Üë'}
                            </button>
                        </div>
                    </div>
                </div>

                {/* Statistics */}
                <div className="stats-section">
                    <div className="stat-card">
                        <div className="stat-number">{stats.totalApplicants}</div>
                        <div className="stat-label">T·ªïng ·ª©ng vi√™n</div>
                    </div>
                    <div className="stat-card">
                        <div className="stat-number">{stats.avgRating.toFixed(1)}</div>
                        <div className="stat-label">ƒê√°nh gi√° TB</div>
                    </div>
                    {stats.statusBreakdown.map(status => (
                        <div key={status._id} className="stat-card">
                            <div className="stat-number">{status.count}</div>
                            <div className="stat-label">
                                {applicantStatuses.find(s => s.value === status._id)?.label}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Bulk Actions */}
                {selectedApplicants.length > 0 && (
                    <div className="bulk-actions-bar">
                        <div className="bulk-info">
                            <span>ƒê√£ ch·ªçn {selectedApplicants.length} ·ª©ng vi√™n</span>
                            <button 
                                onClick={() => setSelectedApplicants([])}
                                className="clear-selection"
                            >
                                B·ªè ch·ªçn
                            </button>
                        </div>
                        
                        <div className="bulk-buttons">
                            <button 
                                onClick={() => setShowBulkActions(true)}
                                className="btn-primary"
                            >
                                Thao t√°c h√†ng lo·∫°t
                            </button>
                        </div>
                    </div>
                )}

                {/* Kanban Board */}
                <div className="kanban-board">
                    {applicantStatuses.map(status => (
                        <div key={status.value} className="kanban-column">
                            <div 
                                className="column-header"
                                style={{ borderTopColor: status.color }}
                            >
                                <div className="column-title">
                                    <span className="status-icon" style={{ color: status.color }}>
                                        {status.value === 'new' && 'üÜï'}
                                        {status.value === 'screening' && 'üîç'}
                                        {status.value === 'phone-screen' && 'üìû'}
                                        {status.value === 'interview' && 'ü§ù'}
                                        {status.value === 'offer' && 'üíº'}
                                        {status.value === 'hired' && '‚úÖ'}
                                        {status.value === 'rejected' && '‚ùå'}
                                    </span>
                                    <span>{status.label}</span>
                                </div>
                                <div className="column-count">{getStatusCount(status.value)}</div>
                            </div>
                            
                            <div className="column-content">
                                {groupedApplicants[status.value].map(applicant => (
                                    <ApplicantCard
                                        key={applicant._id}
                                        applicant={applicant}
                                        isSelected={selectedApplicants.includes(applicant._id)}
                                        onSelect={() => handleApplicantSelect(applicant._id)}
                                        onView={() => handleViewApplicant(applicant)}
                                        onStatusChange={(newStatus, note) => 
                                            handleStatusChange(applicant._id, newStatus, note)
                                        }
                                    />
                                ))}
                                
                                {groupedApplicants[status.value].length === 0 && (
                                    <div className="empty-column">
                                        <p>Kh√¥ng c√≥ ·ª©ng vi√™n</p>
                                    </div>
                                )}
                            </div>
                        </div>
                    ))}
                </div>

                {/* Select All */}
                <div className="select-all-bar">
                    <label className="select-all-checkbox">
                        <input
                            type="checkbox"
                            checked={selectedApplicants.length === applicants.length && applicants.length > 0}
                            onChange={handleSelectAll}
                        />
                        <span>Ch·ªçn t·∫•t c·∫£ ({applicants.length})</span>
                    </label>
                </div>
            </div>

            <PartGOFooter />

            {/* Modals */}
            <ApplicantDetailModal
                isOpen={showDetailModal}
                onClose={() => {
                    setShowDetailModal(false);
                    setSelectedApplicant(null);
                }}
                applicant={selectedApplicant}
                onStatusChange={handleStatusChange}
                onRefresh={loadApplicants}
            />

            <BulkActionsModal
                isOpen={showBulkActions}
                onClose={() => setShowBulkActions(false)}
                selectedCount={selectedApplicants.length}
                onBulkAction={handleBulkStatusChange}
            />
        </div>
    );
};

export default ApplicantDashboard;
