import React, { useState } from 'react';
import { applicantStatuses } from '../services/applicantAPI';
import './BulkActionsModal.css';

const BulkActionsModal = ({
    isOpen,
    onClose,
    selectedCount,
    onBulkAction
}) => {
    const [selectedStatus, setSelectedStatus] = useState('');
    const [note, setNote] = useState('');

    if (!isOpen) return null;

    const handleSubmit = () => {
        if (!selectedStatus) return;

        onBulkAction(selectedStatus, note.trim() || null);
        setSelectedStatus('');
        setNote('');
    };

    const handleClose = () => {
        setSelectedStatus('');
        setNote('');
        onClose();
    };

    return (
        <div className="modal-overlay" onClick={handleClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <h2>Thao tác hàng loạt</h2>
                    <button className="close-btn" onClick={handleClose}>×</button>
                </div>

                <div className="modal-body">
                    <div className="selected-info">
                        <span className="selected-count">{selectedCount}</span>
                        <span>ứng viên được chọn</span>
                    </div>

                    <div className="form-group">
                        <label>Chọn trạng thái mới:</label>
                        <select
                            value={selectedStatus}
                            onChange={(e) => setSelectedStatus(e.target.value)}
                        >
                            <option value="">-- Chọn trạng thái --</option>
                            {applicantStatuses.map(status => (
                                <option key={status.value} value={status.value}>
                                    {status.label}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group">
                        <label>Ghi chú (tùy chọn):</label>
                        <textarea
                            value={note}
                            onChange={(e) => setNote(e.target.value)}
                            placeholder="Nhập ghi chú cho thay đổi này..."
                            rows="3"
                        />
                    </div>

                    <div className="preview-section">
                        <h3>Xem trước:</h3>
                        <div className="preview-text">
                            Sẽ cập nhật <strong>{selectedCount}</strong> ứng viên sang trạng thái{' '}
                            <strong>
                                {applicantStatuses.find(s => s.value === selectedStatus)?.label || '...'}
                            </strong>
                            {note && (
                                <>
                                    {' '}với ghi chú: <em>"{note}"</em>
                                </>
                            )}
                        </div>
                    </div>
                </div>

                <div className="modal-footer">
                    <button
                        className="btn-secondary"
                        onClick={handleClose}
                    >
                        Hủy
                    </button>
                    <button
                        className="btn-primary"
                        onClick={handleSubmit}
                        disabled={!selectedStatus}
                    >
                        Xác nhận
                    </button>
                </div>
            </div>
        </div>
    );
};

export default BulkActionsModal;
