const CompanyBasicInfo = ({ data, onSave, loading }) => {
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        website: '',
        industry: '',
        size: '',
        founded: '',
        location: {
            address: '',
            city: '',
            country: 'Vietnam'
        },
        contact: {
            phone: '',
            email: '',
            socialMedia: {
                linkedin: '',
                facebook: '',
