import React from 'react';
import { applicantStatuses } from '../services/applicantAPI';
import './ApplicantCard.css';

const ApplicantCard = ({ 
    applicant, 
    isSelected, 
    onSelect, 
    onView, 
    onStatusChange 
}) => {
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('vi-VN');
    };

    const renderStars = (rating) => {
        const stars = [];
        for (let i = 1; i <= 5; i++) {
            stars.push(
                <span 
                    key={i} 
                    className={`star ${i <= rating ? '' : 'empty'}`}
                >
                    ‚òÖ
                </span>
            );
        }
        return stars;
    };

    const getStatusColor = (status) => {
        const statusConfig = applicantStatuses.find(s => s.value === status);
        return statusConfig ? statusConfig.color : '#64748b';
    };

    const handleCardClick = (e) => {
        // Don't trigger view if clicking checkbox or action buttons
        if (e.target.type === 'checkbox' || e.target.closest('.action-btn')) {
            return;
        }
        onView();
    };

    const handleStatusChange = (e) => {
        e.stopPropagation();
        const newStatus = e.target.value;
        onStatusChange(newStatus);
    };

    return (
        <div 
            className={`applicant-card ${isSelected ? 'selected' : ''}`}
            onClick={handleCardClick}
        >
            <input
                type="checkbox"
                checked={isSelected}
                onChange={onSelect}
                className="checkbox"
            />

            <div className="applicant-info">
                <div className="applicant-name">
                    {applicant.personalInfo?.name || 'Ch∆∞a c√≥ t√™n'}
                </div>
                
                <div className="applicant-email">
                    {applicant.personalInfo?.email}
                </div>

                {applicant.rating > 0 && (
                    <div className="applicant-rating">
                        <div className="stars">
                            {renderStars(applicant.rating)}
                        </div>
                        <span className="rating-text">({applicant.rating}/5)</span>
                    </div>
                )}

                {applicant.skills && applicant.skills.length > 0 && (
                    <div className="applicant-skills">
                        {applicant.skills.slice(0, 3).map((skill, index) => (
                            <span key={index} className="skill-tag">
                                {skill}
                            </span>
                        ))}
                        {applicant.skills.length > 3 && (
                            <span className="skill-tag more">
                                +{applicant.skills.length - 3}
                            </span>
                        )}
                    </div>
                )}

                <div className="applicant-meta">
                    <span className="applied-date">
                        {formatDate(applicant.appliedAt)}
                    </span>
                    
                    <div 
                        className="status-indicator"
                        style={{ backgroundColor: getStatusColor(applicant.status) }}
                    >
                        {applicantStatuses.find(s => s.value === applicant.status)?.label}
                    </div>
                </div>
            </div>

            <div className="applicant-actions">
                <button 
                    className="action-btn primary"
                    onClick={(e) => {
                        e.stopPropagation();
                        onView();
                    }}
                >
                    Xem
                </button>
                
                <select 
                    className="action-btn status-select"
                    value={applicant.status}
                    onChange={handleStatusChange}
                >
                    {applicantStatuses.map(status => (
                        <option key={status.value} value={status.value}>
                            {status.label}
                        </option>
                    ))}
                </select>
            </div>

            {applicant.notes && applicant.notes.length > 0 && (
                <div className="notes-indicator">
                    <span className="notes-icon">üìù</span>
                    <span className="notes-count">{applicant.notes.length}</span>
                </div>
            )}

            {applicant.interviews && applicant.interviews.length > 0 && (
                <div className="interviews-indicator">
                    <span className="interviews-icon">üìÖ</span>
                    <span className="interviews-count">{applicant.interviews.length}</span>
                </div>
            )}
        </div>
    );
};

export default ApplicantCard;
