import React from 'react';
import './JobCard.css';

const JobCard = ({ job, selected, onSelect, onEdit, onDelete, onPublish, onClone, viewMode = 'grid' }) => {
    const getStatusBadge = (status) => {
        const statusConfig = {
            published: { label: 'ƒê√£ ƒëƒÉng', class: 'badge-success', icon: '‚úì' },
            draft: { label: 'Nh√°p', class: 'badge-pending', icon: 'üìù' },
            closed: { label: 'ƒê√£ ƒë√≥ng', class: 'badge-error', icon: 'üîí' },
            paused: { label: 'T·∫°m d·ª´ng', class: 'badge-warning', icon: '‚è∏' },
            expired: { label: 'H·∫øt h·∫°n', class: 'badge-pending', icon: '‚è∞' }
        };
        
        const config = statusConfig[status] || statusConfig.draft;
        return (
            <span className={`badge-employer ${config.class}`}>
                <span>{config.icon}</span>
                {config.label}
            </span>
        );
    };

    const getTypeBadge = (type) => {
        const typeLabels = {
            'full-time': 'To√†n th·ªùi gian',
            'part-time': 'B√°n th·ªùi gian',
            'freelance': 'Freelance',
            'remote': 'Remote',
            'contract': 'H·ª£p ƒë·ªìng',
            'internship': 'Th·ª±c t·∫≠p'
        };
        return typeLabels[type] || type;
    };

    const getLevelBadge = (level) => {
        const levelLabels = {
            'intern': 'Th·ª±c t·∫≠p sinh',
            'fresher': 'Fresher',
            'junior': 'Junior',
            'middle': 'Middle',
            'senior': 'Senior',
            'lead': 'Lead',
            'manager': 'Manager'
        };
        return levelLabels[level] || level;
    };

    const formatSalary = (salary) => {
        if (!salary) return 'Th·ªèa thu·∫≠n';
        if (salary.type === 'negotiable') return 'Th·ªèa thu·∫≠n';
        
        const formatNumber = (num) => {
            if (num >= 1000000) {
                return `${(num / 1000000).toFixed(1)}M`;
            }
            if (num >= 1000) {
                return `${(num / 1000).toFixed(0)}K`;
            }
            return num;
        };

        return `${formatNumber(salary.min)} - ${formatNumber(salary.max)} ${salary.currency}`;
    };

    const formatDate = (date) => {
        if (!date) return '';
        const d = new Date(date);
        return d.toLocaleDateString('vi-VN', { day: '2-digit', month: '2-digit', year: 'numeric' });
    };

    const handleCheckboxClick = (e) => {
        e.stopPropagation();
        onSelect(!selected);
    };

    return (
        <div className={`job-card-employer ${selected ? 'selected' : ''} ${viewMode}`}>
            {/* Checkbox */}
            <div className="job-card-checkbox">
                <input
                    type="checkbox"
                    checked={selected}
                    onChange={handleCheckboxClick}
                    onClick={(e) => e.stopPropagation()}
                />
            </div>

            {/* Header */}
            <div className="job-card-header">
                <div className="job-card-title-section">
                    <h3 className="job-card-title">{job.title}</h3>
                    <div className="job-card-badges">
                        {getStatusBadge(job.status)}
                        {job.isUrgent && <span className="badge-employer badge-warning">üî• G·∫•p</span>}
                        {job.isHot && <span className="badge-employer badge-error">‚≠ê Hot</span>}
                    </div>
                </div>
            </div>

            {/* Meta Info */}
            <div className="job-card-meta">
                <div className="job-card-meta-item">
                    <span className="meta-icon">üìç</span>
                    <span>{job.location?.city || 'Ch∆∞a x√°c ƒë·ªãnh'}</span>
                </div>
                <div className="job-card-meta-item">
                    <span className="meta-icon">üíº</span>
                    <span>{getTypeBadge(job.type)}</span>
                </div>
                <div className="job-card-meta-item">
                    <span className="meta-icon">üìä</span>
                    <span>{getLevelBadge(job.level)}</span>
                </div>
                <div className="job-card-meta-item">
                    <span className="meta-icon">üí∞</span>
                    <span>{formatSalary(job.salary)}</span>
                </div>
            </div>

            {/* Stats */}
            <div className="job-card-stats">
                <div className="job-stat-item">
                    <div className="job-stat-value">{job.views || 0}</div>
                    <div className="job-stat-label">L∆∞·ª£t xem</div>
                </div>
                <div className="job-stat-item">
                    <div className="job-stat-value">{job.applied || 0}</div>
                    <div className="job-stat-label">·ª®ng tuy·ªÉn</div>
                </div>
                <div className="job-stat-item">
                    <div className="job-stat-value">{job.capacity || 0}</div>
                    <div className="job-stat-label">C·∫ßn tuy·ªÉn</div>
                </div>
            </div>

            {/* Dates */}
            <div className="job-card-dates">
                <div className="job-date-item">
                    <span className="date-label">T·∫°o:</span>
                    <span className="date-value">{formatDate(job.createdAt)}</span>
                </div>
                {job.deadline && (
                    <div className="job-date-item">
                        <span className="date-label">H·∫°n:</span>
                        <span className="date-value">{formatDate(job.deadline)}</span>
                    </div>
                )}
            </div>

            {/* Actions */}
            <div className="job-card-actions">
                <button
                    className="btn-employer btn-employer-sm btn-employer-primary"
                    onClick={(e) => {
                        e.stopPropagation();
                        onEdit(job);
                    }}
                >
                    ‚úèÔ∏è S·ª≠a
                </button>
                
                {job.status === 'draft' && (
                    <button
                        className="btn-employer btn-employer-sm btn-employer-success"
                        onClick={(e) => {
                            e.stopPropagation();
                            onPublish(job._id);
                        }}
                    >
                        üì§ ƒêƒÉng
                    </button>
                )}
                
                <button
                    className="btn-employer btn-employer-sm btn-employer-secondary"
                    onClick={(e) => {
                        e.stopPropagation();
                        onClone(job._id);
                    }}
                >
                    üìã Sao ch√©p
                </button>
                
                <button
                    className="btn-employer btn-employer-sm btn-employer-danger"
                    onClick={(e) => {
                        e.stopPropagation();
                        onDelete(job._id);
                    }}
                >
                    üóëÔ∏è X√≥a
                </button>
            </div>
        </div>
    );
};

export default JobCard;
