            { title: 'Gia sÆ° ToÃ¡n', company: 'Trung tÃ¢m Gia sÆ° HÃ²a Láº¡c', location: 'HÃ²a Láº¡c, HÃ  Ná»™i', tags: ['GiÃ¡o dá»¥c'], logo: 'ðŸ“š', color: '#1db954' },
            { title: 'NhÃ¢n viÃªn vÄƒn phÃ²ng', company: 'CÃ´ng ty TNHH HÃ²a Láº¡c', location: 'HÃ²a Láº¡c, HÃ  Ná»™i', tags: ['Part-Time', 'VÄƒn phÃ²ng'], logo: 'ðŸ’¼', color: '#0061ff' },
