            { title: 'NhÃ¢n viÃªn IT', company: 'CÃ´ng ty CÃ´ng nghá»‡ HÃ²a Láº¡c', location: 'HÃ²a Láº¡c, HÃ  Ná»™i', tags: ['CÃ´ng nghá»‡'], logo: 'ðŸ’»', color: '#623ce4' },
            { title: 'NhÃ¢n viÃªn marketing', company: 'CÃ´ng ty Du lá»‹ch HÃ²a Láº¡c', location: 'HÃ²a Láº¡c, HÃ  Ná»™i', tags: ['Marketing'], logo: 'ðŸ“¢', color: '#1c1c1c' },
