            }
            setCvFile(file);
        };

        const uploadCv = async () => {
            if (!cvFile) return;
            try {
                setCvUploading(true);
                const form = new FormData();
                form.append('file', cvFile);
                const resp = await api.post('/upload', form, { headers: { 'Content-Type': 'multipart/form-data' } });
                const path = resp?.data?.path;
                if (path) {
                    setCvUrl(path);
                    success('Táº£i CV thÃ nh cÃ´ng', 'File Ä‘Ã£ Ä‘Æ°á»£c táº£i lÃªn');
                } else {
                    showError('Táº£i CV tháº¥t báº¡i', 'Lá»—i táº£i file');
                }
            } catch (err) {
                showError('Lá»—i táº£i CV: ' + (err?.message || 'Unknown'), 'Lá»—i há»‡ thá»‘ng');
            } finally {
                setCvUploading(false);
            }
        };

        const handleSubmit = async (e) => {
            e.preventDefault();
            if (!user) {
                warning('Vui lÃ²ng Ä‘Äƒng nháº­p Ä‘á»ƒ á»©ng tuyá»ƒn.', 'Cáº§n Ä‘Äƒng nháº­p');
                if (onShowLogin) onShowLogin();
                return;
            }
            try {
                setSubmitting(true);
                setSubmitError('');
                const isValidObjectId = typeof jobDetail.id === 'string' && /^[a-fA-F0-9]{24}$/.test(jobDetail.id);
