import React, { useState, useEffect } from 'react';
import Header from './Header';
import PartGOFooter from './PartGOFooter ';
import { useAuth } from '../contexts/AuthContext';
import { fetchJobs } from '../services/jobsAPI';
import { getCategoryIcon, getJobTypeIcon, getPriorityColor, getPriorityBadge } from '../utils/jobCategoryIcons';
import './PartGOJobsPage.css';
const PartGOJobsPage = ({ onBackToHome, onSelectJob, onShowLogin, onShowSignUp }) => {
    const { logout } = useAuth();
    const [searchKeyword, setSearchKeyword] = useState('');
    const [location, setLocation] = useState('HÃ²a Láº¡c, HÃ  Ná»™i');
    const [sortBy, setSortBy] = useState('PhÃ¹ há»£p nháº¥t');
    const [selectedFilters, setSelectedFilters] = useState({
        employmentType: [],
        categories: [],
        jobLevel: [],
        salaryRange: []
    });

    // Advanced search states
    const [distanceKm, setDistanceKm] = useState(50); // Khoáº£ng cÃ¡ch tá»« trÆ°á»ng/kÃ½ tÃºc xÃ¡ (tÄƒng cho part-time)
    const [scheduleSelected, setScheduleSelected] = useState([]); // Lá»‹ch lÃ m viá»‡c phÃ¹ há»£p lá»‹ch há»c
    const [salaryMin, setSalaryMin] = useState(20000);
    const [salaryMax, setSalaryMax] = useState(100000);
    const [typeSelected, setTypeSelected] = useState([]);
    const [skillsQuery, setSkillsQuery] = useState('');
    const [userCoords, setUserCoords] = useState(null);
    const [geoError, setGeoError] = useState('');

    useEffect(() => {
        // Thá»­ láº¥y vá»‹ trÃ­ ngÆ°á»i dÃ¹ng khi vÃ o trang
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (pos) => {
                    setUserCoords({ lat: pos.coords.latitude, lng: pos.coords.longitude });
                },
                (err) => {
                    setGeoError('KhÃ´ng thá»ƒ láº¥y vá»‹ trÃ­: ' + err.message);
                },
                { enableHighAccuracy: true, timeout: 8000 }
            );
        } else {
            setGeoError('TrÃ¬nh duyá»‡t khÃ´ng há»— trá»£ Ä‘á»‹nh vá»‹.');
        }
    }, []);

    const [apiJobs, setApiJobs] = useState([]);
    const [loadingJobs, setLoadingJobs] = useState(true);
    const [jobsError, setJobsError] = useState('');
    const [totalJobs, setTotalJobs] = useState(0);

    useEffect(() => {
        (async () => {
            try {
                setLoadingJobs(true);
                setJobsError('');
                const data = await fetchJobs({ page: 1, limit: 50 });
                const raw = Array.isArray(data?.jobs) ? data.jobs : [];
                setTotalJobs(parseInt(data?.total || raw.length || 0));
                // HÃ m táº¡o tÃªn Ä‘á»‹a Ä‘iá»ƒm hiá»ƒn thá»‹ thÃ¢n thiá»‡n
                const getDisplayLocation = (locationData) => {
                    if (!locationData) return 'HÃ²a Láº¡c, HÃ  Ná»™i';

                    // Náº¿u cÃ³ city, Æ°u tiÃªn hiá»ƒn thá»‹ city
                    if (locationData.city) {
                        return locationData.city;
                    }

                    // Náº¿u address ngáº¯n (< 20 kÃ½ tá»±), hiá»ƒn thá»‹ address
                    if (locationData.address && locationData.address.length < 20) {
                        return locationData.address;
                    }

                    // Náº¿u address dÃ i, cá»‘ gáº¯ng rÃºt gá»n
                    if (locationData.address) {
                        const address = locationData.address;

                        // TÃ¬m tÃªn khu vá»±c chÃ­nh
                        const patterns = [
                            /hÃ²a láº¡c/i,
                            /hoa lac/i,
                            /tháº¡ch tháº¥t/i,
                            /thach that/i,
                            /tÃ¢n xÃ£/i,
                            /tan xa/i,
                            /cáº§u giáº¥y/i,
                            /cau giay/i,
                            /Ä‘á»‘ng Ä‘a/i,
                            /dong da/i,
                            /hoÃ n kiáº¿m/i,
                            /hoan kiem/i,
                            /thanh xuÃ¢n/i,
                            /thanh xuan/i,
                            /quáº­n \d+/i,
                            /quan \d+/i
                        ];

                        for (const pattern of patterns) {
                            const match = address.match(pattern);
                            if (match) {
                                return match[0];
                            }
                        }

                        // Náº¿u khÃ´ng tÃ¬m tháº¥y pattern, láº¥y pháº§n Ä‘áº§u cá»§a address
                        const parts = address.split(',');
                        if (parts.length > 1) {
                            return parts[0].trim();
                        }

                        return address;
                    }

                    return 'HÃ²a Láº¡c, HÃ  Ná»™i';
                };

                const mapped = raw.map((j) => ({
                    id: j._id || j.id,
                    title: j.title || 'CÃ´ng viá»‡c',
                    company: j.company?.name || j.companyName || 'CÃ´ng ty',
                    location: getDisplayLocation(j.location),
                    fullAddress: j.location?.address, // LÆ°u Ä‘á»‹a chá»‰ Ä‘áº§y Ä‘á»§
                    type: (j.type || j.employmentType || '').toString(),
                    tags: Array.isArray(j.tags) ? j.tags : [],
                    applied: j.applied || 0,
                    capacity: j.capacity || 10,
                    logo: 'ðŸ’¼',
                    color: '#1f2937',
                    hourlyRate: j.salary ? `${j.salary.min?.toLocaleString()}-${j.salary.max?.toLocaleString()} VNÄ/giá»` : 'Thá»a thuáº­n',
                    workSchedule: j.schedule?.workHours ? `${j.schedule.workHours.start}-${j.schedule.workHours.end}` : (j.schedule || 'Linh hoáº¡t'),
                    postedDate: j.createdAt ? new Date(j.createdAt).toLocaleDateString('vi-VN') : '',
                    lat: j.location?.coordinates?.lat,
                    lng: j.location?.coordinates?.lng,
                }));
                setApiJobs(mapped);
            } catch (e) {
                setJobsError(e?.message || 'KhÃ´ng táº£i Ä‘Æ°á»£c danh sÃ¡ch viá»‡c lÃ m');
            } finally {
                setLoadingJobs(false);
            }
        })();
    }, []);

    const jobs = apiJobs;

    const filterOptions = {
        employmentType: [
            { name: 'Part-time', count: 15, selected: true },
            { name: 'Full-time', count: 3 },
            { name: 'Remote', count: 8 },
            { name: 'Thá»±c táº­p', count: 24 },
            { name: 'Há»£p Ä‘á»“ng', count: 3 },
            { name: 'Freelance', count: 12 }
        ],
        workSchedule: [
            { name: 'Ca sÃ¡ng (6h-12h)', count: 8 },
            { name: 'Ca chiá»u (12h-18h)', count: 12 },
            { name: 'Ca tá»‘i (18h-22h)', count: 6 },
            { name: 'Ca Ä‘Ãªm (22h-6h)', count: 3 },
            { name: 'Cuá»‘i tuáº§n', count: 9 },
            { name: 'Linh hoáº¡t', count: 15 }
        ],
        hourlyRate: [
            { name: '20,000-30,000 VNÄ/giá»', count: 8 },
            { name: '30,000-40,000 VNÄ/giá»', count: 12 },
            { name: '40,000-50,000 VNÄ/giá»', count: 6 },
            { name: '50,000-60,000 VNÄ/giá»', count: 4 },
            { name: '60,000+ VNÄ/giá»', count: 2 }
        ],
        categories: [
            { name: 'BÃ¡n hÃ ng', count: 24 },
            { name: 'Phá»¥c vá»¥', count: 3 },
            { name: 'Marketing', count: 3 },
            { name: 'VÄƒn phÃ²ng', count: 3, selected: true },
            { name: 'GiÃ¡o dá»¥c', count: 6 },
            { name: 'CÃ´ng nghá»‡', count: 4 },
            { name: 'Dá»‹ch vá»¥', count: 4 },
            { name: 'Giao hÃ ng', count: 5 }
        ],
        jobLevel: [
            { name: 'Má»›i tá»‘t nghiá»‡p', count: 57 },
            { name: 'CÃ³ kinh nghiá»‡m', count: 3 },
            { name: 'Kinh nghiá»‡m cao', count: 5 },
            { name: 'Quáº£n lÃ½', count: 12, selected: true },
            { name: 'Cáº¥p cao', count: 8 }
        ],
        salaryRange: [
            { name: '2-3 triá»‡u/thÃ¡ng', count: 4 },
            { name: '3-4 triá»‡u/thÃ¡ng', count: 6 },
            { name: '4-5 triá»‡u/thÃ¡ng', count: 10 },
            { name: '5 triá»‡u+/thÃ¡ng', count: 4, selected: true }
        ]
    };

    // Helpers
    const parseSalary = (rateStr) => {
        // '25,000-35,000 VNÄ/giá»' => [25000, 35000]
        if (!rateStr || rateStr === 'Thá»a thuáº­n') return [0, 999999];

        const match = rateStr.match(/([0-9.,]+)\s*-\s*([0-9.,]+)/);
        if (!match) return [0, 999999];

        const toNum = (s) => {
            const cleaned = s.replace(/\./g, '').replace(/,/g, '');
            return parseInt(cleaned) || 0;
        };

        return [toNum(match[1]), toNum(match[2])];
    };

    const skillsFromQuery = (q) => q.split(',').map(s => s.trim()).filter(Boolean);

    const saveJob = (job) => {
        try {
            const existing = JSON.parse(localStorage.getItem('savedJobs') || '[]');
            const minimal = {
                id: job.id,
                title: job.title,
                company: job.company,
                location: job.location,
                hourlyRate: job.hourlyRate,
                savedDate: new Date().toLocaleDateString('vi-VN')
            };
            const next = [minimal, ...existing.filter(j => j.id !== job.id)];
            localStorage.setItem('savedJobs', JSON.stringify(next));
            alert('ÄÃ£ lÆ°u viá»‡c lÃ m vÃ o danh sÃ¡ch');
        } catch (e) {
            alert('KhÃ´ng thá»ƒ lÆ°u viá»‡c lÃ m');
        }
    };

    const openMapForJob = (job) => {
        console.log('ðŸ—ºï¸ Opening map for job:', {
            jobId: job.id,
            title: job.title,
            lat: job.lat,
            lng: job.lng,
            userCoords,
            fullJob: job
        });

        if (job.lat && job.lng) {
            const dest = `${job.lat},${job.lng}`;
            if (userCoords) {
                const origin = `${userCoords.lat},${userCoords.lng}`;
                const url = `https://www.google.com/maps/dir/?api=1&origin=${encodeURIComponent(origin)}&destination=${encodeURIComponent(dest)}&travelmode=driving`;
                console.log('ðŸ—ºï¸ Opening directions:', url);
                window.open(url, '_blank');
            } else {
                const url = `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(dest)}`;
                console.log('ðŸ—ºï¸ Opening location:', url);
                window.open(url, '_blank');
            }
        } else {
            console.error('âŒ KhÃ´ng cÃ³ tá»a Ä‘á»™ cho cÃ´ng viá»‡c nÃ y!', {
                jobId: job.id,
                title: job.title,
                lat: job.lat,
                lng: job.lng
            });
            alert('KhÃ´ng cÃ³ thÃ´ng tin vá»‹ trÃ­ cho cÃ´ng viá»‡c nÃ y. Vui lÃ²ng liÃªn há»‡ nhÃ  tuyá»ƒn dá»¥ng Ä‘á»ƒ biáº¿t thÃªm chi tiáº¿t.');
        }
    };

    const haversineKm = (lat1, lon1, lat2, lon2) => {
        const toRad = (v) => (v * Math.PI) / 180;
        const R = 6371; // km
        const dLat = toRad(lat2 - lat1);
        const dLon = toRad(lon2 - lon1);
        const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
            Math.cos(toRad(lat1)) * Math.cos(toRad(lat2)) *
            Math.sin(dLon / 2) * Math.sin(dLon / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        return R * c;
    };

    const filteredJobs = jobs.filter((job) => {
        // Keyword
        const keywordOk = !searchKeyword || (job.title + ' ' + job.company + ' ' + job.tags.join(' ')).toLowerCase().includes(searchKeyword.toLowerCase());

        // Distance - Táº M THá»œI Táº®T Ä‘á»ƒ hiá»ƒn thá»‹ táº¥t cáº£ jobs
        // Sáº½ chá»‰ lá»c khi user chá»n cá»¥ thá»ƒ "Gáº§n tÃ´i" hoáº·c nháº­p Ä‘á»‹a chá»‰
        let distanceOk = true;

        // Chá»‰ tÃ­nh distance Ä‘á»ƒ hiá»ƒn thá»‹, khÃ´ng lá»c
        if (userCoords && job.lat && job.lng) {
            const d = haversineKm(userCoords.lat, userCoords.lng, job.lat, job.lng);
            job._distanceDisplay = d.toFixed(1);

            console.log(`ðŸ“ Distance info for "${job.title}":`, {
                jobLocation: job.location || 'N/A',
                jobCoords: { lat: job.lat, lng: job.lng },
                distance: d.toFixed(1) + 'km',
                note: 'Distance calculated but NOT filtering'
            });
        } else {
            job._distanceDisplay = undefined;
            console.log(`ðŸ“ No distance calc for "${job.title}":`, {
                hasUserCoords: !!userCoords,
                hasJobCoords: !!(job.lat && job.lng),
                jobLocation: job.location || 'N/A'
            });
        }

        // Schedule
        const scheduleOk = scheduleSelected.length === 0 || scheduleSelected.some(s => job.workSchedule.includes(s));

        // Type
        const typeOk = typeSelected.length === 0 || typeSelected.map(s => s.toLowerCase()).includes((job.type || '').toLowerCase());

        // Salary
        const [minR, maxR] = parseSalary(job.hourlyRate);
        const salaryOk = maxR >= salaryMin && minR <= salaryMax;

        // Skills
        const querySkills = skillsFromQuery(skillsQuery);
        const jobText = (job.title + ' ' + job.tags.join(' ')).toLowerCase();
        const skillsOk = querySkills.length === 0 || querySkills.every(s => jobText.includes(s.toLowerCase()));

        const result = keywordOk && distanceOk && scheduleOk && typeOk && salaryOk && skillsOk;

        // Debug logging
        if (!result) {
            console.log(`ðŸ” Job "${job.title}" filtered out:`, {
                keywordOk,
                distanceOk,
                scheduleOk,
                typeOk,
                salaryOk,
                skillsOk,
                job
            });
        }

        return result;
    });

    console.log('ðŸ“Š Filter results:', {
        totalJobs: jobs.length,
        filteredJobs: filteredJobs.length,
        filters: {
            searchKeyword,
            distanceKm,
            scheduleSelected,
            typeSelected,
            salaryMin,
            salaryMax,
            skillsQuery
        }
    });

    // Enhanced Job Card Component
    const ModernJobCard = ({ job }) => {
        const [isSaved, setIsSaved] = useState(false);

        const getBadgeClass = (type) => {
            switch (type?.toLowerCase()) {
                case 'part-time': return 'badge-part-time';
                case 'full-time': return 'badge-full-time';
                case 'remote': return 'badge-remote';
                default: return 'badge-part-time';
            }
        };

        const getCompanyLogo = (company) => {
            // Generate consistent colors based on company name
            const colors = ['#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD', '#98D8C8', '#F7DC6F'];
            const colorIndex = company.charCodeAt(0) % colors.length;
            return {
                background: colors[colorIndex],
                text: company.charAt(0).toUpperCase()
            };
        };

        const logo = getCompanyLogo(job.company);

        return (
            <div className="job-card-enhanced" onClick={() => onSelectJob(job.id)}>
                {/* Company Logo */}
                <div className="job-logo-section">
                    <div className="company-logo" style={{ backgroundColor: logo.background }}>
                        {logo.text}
                    </div>
                    <div className="job-status-indicator"></div>
                </div>

                {/* Job Header */}
                <div className="job-header-enhanced">
                    <div className="job-title-section">
                        <div className="job-title-with-icon">
                            <span className="job-category-icon" style={{ fontSize: '20px' }}>
                                {getCategoryIcon(job.category).emoji}
                            </span>
                            <div>
                                <h3 className="job-title-enhanced">{job.title}</h3>
                                <div className="job-company-enhanced">{job.company}</div>
                            </div>
                        </div>
                    </div>
                    <div className="job-badges-enhanced">
                        <span className={`job-badge-enhanced ${getBadgeClass(job.type)}`}>
                            {getJobTypeIcon(job.type)} {job.type}
                        </span>
                        {job.priority === 'urgent' || job.priority === 'hot' ? (
                            <span className="job-badge-enhanced badge-urgent-enhanced" style={{ backgroundColor: getPriorityColor(job.priority) }}>
                                {getPriorityBadge(job.priority)}
                            </span>
                        ) : job.priority === 'premium' ? (
                            <span className="job-badge-enhanced badge-premium-enhanced" style={{ backgroundColor: getPriorityColor(job.priority) }}>
                                {getPriorityBadge(job.priority)}
                            </span>
                        ) : null}
                    </div>
                </div>

                {/* Job Details Grid */}
                <div className="job-details-enhanced">
                    <div className="detail-item-enhanced">
                        <div className="detail-icon-enhanced">
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"></path>
                                <circle cx="12" cy="10" r="3"></circle>
                            </svg>
                        </div>
                        <div className="detail-content">
                            <span className="detail-label">Äá»‹a Ä‘iá»ƒm</span>
                            <span className="detail-value">{job.location}</span>
                        </div>
                    </div>

                    <div className="detail-item-enhanced">
                        <div className="detail-icon-enhanced">
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <circle cx="12" cy="12" r="10"></circle>
                                <polyline points="12,6 12,12 16,14"></polyline>
                            </svg>
                        </div>
                        <div className="detail-content">
                            <span className="detail-label">Thá»i gian</span>
                            <span className="detail-value">{job.workSchedule}</span>
                        </div>
                    </div>

                    <div className="detail-item-enhanced">
                        <div className="detail-icon-enhanced">
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <line x1="12" y1="1" x2="12" y2="23"></line>
                                <path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"></path>
                            </svg>
                        </div>
                        <div className="detail-content">
                            <span className="detail-label">LÆ°Æ¡ng</span>
                            <span className="detail-value">{job.hourlyRate}</span>
                        </div>
                    </div>

                    <div className="detail-item-enhanced">
                        <div className="detail-icon-enhanced">
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                                <circle cx="9" cy="7" r="4"></circle>
                                <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
                                <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
                            </svg>
                        </div>
                        <div className="detail-content">
                            <span className="detail-label">á»¨ng viÃªn</span>
                            <span className="detail-value">{job.applied}/{job.capacity}</span>
                        </div>
                    </div>
                </div>

                {/* Job Tags */}
                <div className="job-tags-enhanced">
                    {job.tags.slice(0, 3).map((tag, index) => (
                        <span key={index} className="job-tag-enhanced">{tag}</span>
                    ))}
                    {job.tags.length > 3 && (
                        <span className="job-tag-enhanced more-tags">+{job.tags.length - 3}</span>
                    )}
                </div>

                {/* Job Footer */}
                <div className="job-footer-enhanced">
                    <div className="job-meta-enhanced">
                        <span className="posted-date">ðŸ“… {job.postedDate}</span>
                        <span className="distance-info">ðŸ“ {job._distanceDisplay || 'N/A'} km</span>
                    </div>
                    <div className="job-actions-enhanced">
                        <button
                            className="btn-apply-enhanced"
                            onClick={(e) => {
                                e.stopPropagation();
                                onSelectJob(job.id);
                            }}
                        >
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M16 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                                <circle cx="8.5" cy="7" r="4"></circle>
                                <line x1="20" y1="8" x2="20" y2="14"></line>
                                <line x1="23" y1="11" x2="17" y2="11"></line>
                            </svg>
                            á»¨ng tuyá»ƒn
                        </button>
                        <button
                            className={`btn-save-enhanced ${isSaved ? 'saved' : ''}`}
                            onClick={(e) => {
                                e.stopPropagation();
                                setIsSaved(!isSaved);
                                saveJob(job);
                            }}
                            title="LÆ°u viá»‡c lÃ m"
                        >
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M19 21l-7-5-7 5V5a2 2 0 0 1 2-2h10a2 2 0 0 1 2 2z"></path>
                            </svg>
                        </button>
                        <button
                            className="btn-map-enhanced"
                            onClick={(e) => {
                                e.stopPropagation();
                                openMapForJob(job);
                            }}
                            title="Xem báº£n Ä‘á»“"
                        >
                            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                                <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"></path>
                                <circle cx="12" cy="10" r="3"></circle>
                            </svg>
                        </button>
                    </div>
                </div>
            </div>
        );
    };

    return (
        <div>
            {/* Bootstrap CSS */}
            <link
                href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.3.0/css/bootstrap.min.css"
                rel="stylesheet"
            />

            <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh' }}>

                {/* Header */}
                <Header
                    onOpenCv={() => window.location.href = '/profile/cv'}
