import React from 'react';
import './StatsCards.css';

const StatsCards = ({ stats }) => {
    const statsConfig = [
        {
            id: 'totalJobs',
            title: 'Tổng tin đăng',
            value: stats.totalJobs,
            icon: '📊',
            color: 'blue',
            description: 'Tất cả tin tuyển dụng',
            trend: null
        },
        {
            id: 'activeJobs',
            title: 'Tin đang hoạt động',
            value: stats.activeJobs,
            icon: '✅',
            color: 'green',
            description: 'Đang tuyển dụng',
            trend: null
        },
        {
            id: 'draftJobs',
            title: 'Tin nháp',
            value: stats.draftJobs,
            icon: '📝',
            color: 'orange',
            description: 'Chưa đăng',
            trend: null
        },
        {
            id: 'totalApplications',
            title: 'Tổng ứng tuyển',
            value: stats.totalApplications,
            icon: '👥',
            color: 'purple',
            description: 'Hồ sơ nhận được',
            trend: null
        },
        {
            id: 'pendingApplications',
            title: 'Chờ xử lý',
            value: stats.pendingApplications,
            icon: '⏳',
            color: 'orange',
            description: 'Cần xem xét',
            trend: null
        },
        {
            id: 'totalViews',
            title: 'Lượt xem',
            value: stats.totalViews,
            icon: '👁️',
            color: 'indigo',
            description: 'Tổng lượt xem tin',
            trend: null
        },
        {
            id: 'conversionRate',
            title: 'Tỷ lệ chuyển đổi',
            value: `${stats.conversionRate}%`,
            icon: '📈',
            color: 'teal',
            description: 'Ứng tuyển/Lượt xem',
            trend: null
        }
    ];

    const getColorClass = (color) => {
        const colorMap = {
            blue: 'stat-blue',
            green: 'stat-green',
            purple: 'stat-purple',
            orange: 'stat-orange',
            indigo: 'stat-indigo',
            teal: 'stat-teal',
            red: 'stat-red'
        };
        return colorMap[color] || 'stat-blue';
    };

    const formatNumber = (num) => {
        if (num >= 1000000) {
            return (num / 1000000).toFixed(1) + 'M';
        }
        if (num >= 1000) {
            return (num / 1000).toFixed(1) + 'K';
        }
        return num.toString();
    };

    return (
        <div className="stats-cards">
            {statsConfig.map(stat => (
                <div key={stat.id} className={`stat-card ${getColorClass(stat.color)}`}>
                    <div className="stat-header">
                        <div className="stat-icon">
                            {stat.icon}
                        </div>
                        {stat.trend && (
                            <div className={`stat-trend ${stat.trend > 0 ? 'positive' : 'negative'}`}>
                                {stat.trend > 0 ? '↗️' : '↘️'}
                                {Math.abs(stat.trend)}%
                            </div>
                        )}
                    </div>

                    <div className="stat-content">
                        <div className="stat-value">
                            {typeof stat.value === 'number' ? formatNumber(stat.value) : stat.value}
                        </div>
                        <div className="stat-title">{stat.title}</div>
                        <div className="stat-description">{stat.description}</div>
                    </div>

                    <div className="stat-footer">
                        <button
                            className="stat-action"
                            onClick={() => {
                                // Navigate to relevant page based on stat type
                                switch (stat.id) {
                                    case 'totalJobs':
                                    case 'activeJobs':
                                        window.location.href = '/company-dashboard/jobs';
                                        break;
                                    case 'totalApplications':
                                    case 'pendingApplications':
                                        window.location.href = '/company-dashboard/applications';
                                        break;
                                    case 'totalViews':
                                    case 'conversionRate':
                                        window.location.href = '/company-dashboard/analytics';
                                        break;
                                    default:
                                        break;
                                }
                            }}
                        >
                            Xem chi tiết →
                        </button>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default StatsCards;
