// Header.js
import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import PostJobModal from './PostJobModal';
import EmployerPricingModal from './EmployerPricingModal';

const Header = ({ onOpenCv, onOpenCompanyDashboard, onOpenPostJob, onOpenPricing, onShowLogin, onShowSignUp, onLogout }) => {
  const { user, isAuthenticated, logout } = useAuth();
  console.log('Header - user:', user, 'isAuthenticated:', isAuthenticated);
  const [showPostJob, setShowPostJob] = useState(false);
  const [showPricing, setShowPricing] = useState(false);
  const [showEmployerMenu, setShowEmployerMenu] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);

  const handleLogout = () => {
    if (onLogout) {
      onLogout();
    } else {
      logout();
    }
    setShowUserMenu(false);
  };

  return (
    <div>
      {/* Bootstrap CSS */}
      <link
        href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap/5.3.0/css/bootstrap.min.css"
        rel="stylesheet"
      />

      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm">
        <div className="container-fluid px-4">
          {/* Logo and Brand */}
          <a className="navbar-brand d-flex align-items-center" href="/" onClick={(e) => { e.preventDefault(); window.location.href = '/'; }}>
            <div
              className="rounded-circle me-2 d-flex align-items-center justify-content-center"
              style={{
                width: '40px',
                height: '40px',
                backgroundColor: '#f8f9fa',
                border: '2px solid #e9ecef'
              }}
            >
              <span style={{ fontSize: '18px', color: '#6c757d' }}>üë§</span>
            </div>
            <span
              className="fw-bold"
              style={{
                fontSize: '24px',
                color: '#333'
              }}
            >
              Part GO
            </span>
          </a>

          {/* Toggle button for mobile */}
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            {/* Navigation Links */}
            <ul className="navbar-nav me-auto ms-4">
              <li className="nav-item">
                <a
                  className="nav-link me-3"
                  href="#"
                  style={{
                    color: '#ff6b35',
                    fontWeight: '500'
                  }}
                >
                  T√¨m vi·ªác l√†m
                </a>
              </li>
              {/* Removed: Kh√°m ph√° c√¥ng ty */}
              <li className="nav-item dropdown">
                <a
                  className="nav-link dropdown-toggle me-3"
                  href="#"
                  role="button"
                  onClick={(e) => { e.preventDefault(); setShowEmployerMenu(v => !v); }}
                  style={{
                    color: '#6c757d',
                    fontWeight: '500'
                  }}
                >
                  D√†nh cho nh√† tuy·ªÉn d·ª•ng
                </a>
                <ul className={`dropdown-menu ${showEmployerMenu ? 'show' : ''}`} style={{ position: 'absolute' }}>
                  <li><button className="dropdown-item" onClick={() => { setShowPostJob(true); onOpenPostJob?.(); }}>ƒêƒÉng tuy·ªÉn vi·ªác l√†m</button></li>
                  <li><button className="dropdown-item" onClick={onOpenCompanyDashboard}>B·∫£ng ƒëi·ªÅu khi·ªÉn c√¥ng ty</button></li>
                  <li><button className="dropdown-item" onClick={() => { setShowPricing(true); onOpenPricing?.(); }}>B·∫£ng gi√°</button></li>
                </ul>
              </li>
            </ul>

            {/* Right side buttons */}
            <div className="d-flex align-items-center">
              <button
                className="btn btn-outline-secondary me-3"
                style={{ borderRadius: '6px' }}
                onClick={onOpenCv}
              >
                T·∫°o CV
              </button>

              {isAuthenticated ? (
                // User is logged in - show user menu
                <div className="dropdown">
                  <button
                    className="btn btn-link text-decoration-none d-flex align-items-center"
                    style={{
                      color: '#6c757d',
                      fontWeight: '500',
                      border: 'none'
                    }}
                    onClick={() => setShowUserMenu(!showUserMenu)}
                  >
                    <div
                      className="rounded-circle me-2 d-flex align-items-center justify-content-center"
                      style={{
                        width: '32px',
                        height: '32px',
                        backgroundColor: '#ff6b35',
                        color: 'white',
                        fontSize: '14px'
                      }}
                    >
                      {user?.fullName?.charAt(0)?.toUpperCase() || 'U'}
                    </div>
                    {user?.fullName || 'User'}
                  </button>
                  <ul className={`dropdown-menu ${showUserMenu ? 'show' : ''}`} style={{ position: 'absolute', right: 0 }}>
                    <li><button className="dropdown-item" onClick={onOpenCv}>H·ªì s∆° c√° nh√¢n</button></li>
                    <li><button className="dropdown-item" onClick={onOpenCompanyDashboard}>B·∫£ng ƒëi·ªÅu khi·ªÉn</button></li>
                    <li><hr className="dropdown-divider" /></li>
                    <li><button className="dropdown-item" onClick={handleLogout}>ƒêƒÉng xu·∫•t</button></li>
                  </ul>
                </div>
              ) : (
                // User is not logged in - show login/signup buttons
                <>
                  <button
                    className="btn btn-link text-decoration-none me-3"
                    style={{
                      color: '#6c757d',
                      fontWeight: '500',
                      border: 'none'
                    }}
                    onClick={onShowLogin}
                  >
                    ƒêƒÉng nh·∫≠p
                  </button>
                  <button
                    className="btn px-4 py-2"
                    style={{
                      backgroundColor: '#ff6b35',
                      borderColor: '#ff6b35',
                      color: 'white',
                      fontWeight: '500',
                      borderRadius: '6px'
                    }}
                    onClick={onShowSignUp}
                  >
                    ƒêƒÉng k√Ω
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      </nav>

      <PostJobModal isOpen={showPostJob} onClose={() => setShowPostJob(false)} />
      <EmployerPricingModal isOpen={showPricing} onClose={() => setShowPricing(false)} />

      {/* Test Toast Button - Remove after testing */}
      <div style={{ position: 'fixed', bottom: '20px', left: '20px', zIndex: 9999 }}>
        <button
          onClick={() => {
            console.log('Testing toast...');
            success('Test Toast ho·∫°t ƒë·ªông!', 'Th√†nh c√¥ng');
          }}
          style={{
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer'
          }}
        >
          Test Toast
        </button>
      </div>
    </div>
  );
};

export default Header;
