import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNotification } from '../contexts/NotificationContext';
import { applicantAPI, applicantStatuses, interviewTypes, interviewStatuses, offerStatuses } from '../services/applicantAPI';
import './ApplicantDetailModal.css';

const ApplicantDetailModal = ({ 
    isOpen, 
    onClose, 
    applicant, 
    onStatusChange, 
    onRefresh 
}) => {
    const { user } = useAuth();
    const { success, error: showError } = useNotification();
    
    const [activeTab, setActiveTab] = useState('overview');
    const [newNote, setNewNote] = useState('');
    const [isPrivateNote, setIsPrivateNote] = useState(false);
    const [rating, setRating] = useState(0);
    const [showInterviewForm, setShowInterviewForm] = useState(false);
    const [showOfferForm, setShowOfferForm] = useState(false);
    const [interviewForm, setInterviewForm] = useState({
        type: 'phone',
        scheduledAt: '',
        duration: 30,
        location: '',
        meetingLink: ''
    });
    const [offerForm, setOfferForm] = useState({
        salary: { amount: '', currency: 'VND', period: 'monthly' },
        startDate: '',
        benefits: [],
        expiresAt: '',
        notes: ''
    });

    useEffect(() => {
        if (applicant) {
            setRating(applicant.rating || 0);
        }
    }, [applicant]);

    if (!isOpen || !applicant) return null;

    const handleAddNote = async () => {
        if (!newNote.trim()) return;

        try {
            await applicantAPI.addNote(applicant._id, newNote, isPrivateNote);
            success('Đã thêm ghi chú', 'Thành công');
            setNewNote('');
            setIsPrivateNote(false);
            onRefresh();
        } catch (error) {
            showError('Lỗi khi thêm ghi chú: ' + (error.message || 'Unknown error'), 'Lỗi');
        }
    };

    const handleUpdateRating = async (newRating) => {
        try {
            await applicantAPI.updateRating(applicant._id, newRating);
            setRating(newRating);
            success('Đã cập nhật đánh giá', 'Thành công');
            onRefresh();
        } catch (error) {
            showError('Lỗi khi cập nhật đánh giá: ' + (error.message || 'Unknown error'), 'Lỗi');
        }
    };

    const handleScheduleInterview = async () => {
        try {
            await applicantAPI.scheduleInterview(applicant._id, interviewForm);
            success('Đã lên lịch phỏng vấn', 'Thành công');
            setShowInterviewForm(false);
            setInterviewForm({
                type: 'phone',
                scheduledAt: '',
                duration: 30,
                location: '',
                meetingLink: ''
            });
            onRefresh();
        } catch (error) {
            showError('Lỗi khi lên lịch phỏng vấn: ' + (error.message || 'Unknown error'), 'Lỗi');
        }
    };

    const handleCreateOffer = async () => {
        try {
            await applicantAPI.createOffer(applicant._id, offerForm);
            success('Đã tạo đề nghị làm việc', 'Thành công');
            setShowOfferForm(false);
            setOfferForm({
                salary: { amount: '', currency: 'VND', period: 'monthly' },
                startDate: '',
                benefits: [],
                expiresAt: '',
                notes: ''
            });
            onRefresh();
        } catch (error) {
            showError('Lỗi khi tạo đề nghị: ' + (error.message || 'Unknown error'), 'Lỗi');
        }
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return date.toLocaleDateString('vi-VN', {
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        });
    };

    const renderStars = (currentRating) => {
        const stars = [];
        for (let i = 1; i <= 5; i++) {
            stars.push(
                <span 
                    key={i} 
                    className={`star ${i <= currentRating ? 'filled' : 'empty'}`}
                    onClick={() => handleUpdateRating(i)}
                >
                    ★
                </span>
            );
        }
        return stars;
    };

    const getStatusConfig = (status) => {
        return applicantStatuses.find(s => s.value === status) || 
               { label: status, color: '#64748b' };
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <div className="applicant-header">
                        <div className="applicant-avatar">
                            {applicant.personalInfo?.name?.charAt(0) || 'A'}
                        </div>
                        <div className="applicant-info">
                            <h2>{applicant.personalInfo?.name || 'Chưa có tên'}</h2>
                            <p>{applicant.personalInfo?.email}</p>
                            <div className="status-badge" style={{ 
                                backgroundColor: getStatusConfig(applicant.status).color 
                            }}>
                                {getStatusConfig(applicant.status).label}
                            </div>
                        </div>
                    </div>
                    
                    <div className="rating-section">
                        <div className="rating-label">Đánh giá:</div>
                        <div className="stars">
                            {renderStars(rating)}
                        </div>
                    </div>
                    
                    <button className="close-btn" onClick={onClose}>×</button>
                </div>

                <div className="modal-body">
                    <div className="tabs">
                        <button 
                            className={`tab ${activeTab === 'overview' ? 'active' : ''}`}
                            onClick={() => setActiveTab('overview')}
                        >
                            Tổng quan
                        </button>
                        <button 
                            className={`tab ${activeTab === 'experience' ? 'active' : ''}`}
                            onClick={() => setActiveTab('experience')}
                        >
                            Kinh nghiệm
                        </button>
                        <button 
                            className={`tab ${activeTab === 'interviews' ? 'active' : ''}`}
                            onClick={() => setActiveTab('interviews')}
                        >
                            Phỏng vấn
                        </button>
                        <button 
                            className={`tab ${activeTab === 'notes' ? 'active' : ''}`}
                            onClick={() => setActiveTab('notes')}
                        >
                            Ghi chú
                        </button>
                    </div>

                    <div className="tab-content">
                        {activeTab === 'overview' && (
                            <div className="overview-tab">
                                <div className="info-grid">
                                    <div className="info-section">
                                        <h3>Thông tin cá nhân</h3>
                                        <div className="info-item">
                                            <label>Email:</label>
                                            <span>{applicant.personalInfo?.email}</span>
                                        </div>
                                        <div className="info-item">
                                            <label>Số điện thoại:</label>
                                            <span>{applicant.personalInfo?.phone || 'Chưa cung cấp'}</span>
                                        </div>
                                        <div className="info-item">
                                            <label>Địa chỉ:</label>
                                            <span>{applicant.personalInfo?.location || 'Chưa cung cấp'}</span>
                                        </div>
                                        <div className="info-item">
                                            <label>LinkedIn:</label>
                                            <span>{applicant.personalInfo?.linkedin || 'Chưa cung cấp'}</span>
                                        </div>
                                    </div>

                                    <div className="info-section">
                                        <h3>Kỹ năng</h3>
                                        <div className="skills-list">
                                            {applicant.skills?.map((skill, index) => (
                                                <span key={index} className="skill-tag">
                                                    {skill}
                                                </span>
                                            ))}
                                        </div>
                                    </div>

                                    <div className="info-section">
                                        <h3>Thông tin ứng tuyển</h3>
                                        <div className="info-item">
                                            <label>Ngày nộp:</label>
                                            <span>{formatDate(applicant.appliedAt)}</span>
                                        </div>
                                        <div className="info-item">
                                            <label>Nguồn:</label>
                                            <span>{applicant.source || 'Website'}</span>
                                        </div>
                                    </div>
                                </div>

                                {applicant.coverLetter && (
                                    <div className="cover-letter">
                                        <h3>Thư xin việc</h3>
                                        <div className="cover-letter-content">
                                            {applicant.coverLetter}
                                        </div>
                                    </div>
                                )}

                                <div className="actions">
                                    <button 
                                        className="btn-primary"
                                        onClick={() => setShowInterviewForm(true)}
                                    >
                                        Lên lịch phỏng vấn
                                    </button>
                                    <button 
                                        className="btn-secondary"
                                        onClick={() => setShowOfferForm(true)}
                                    >
                                        Tạo đề nghị
                                    </button>
                                </div>
                            </div>
                        )}

                        {activeTab === 'experience' && (
                            <div className="experience-tab">
                                <div className="experience-section">
                                    <h3>Kinh nghiệm làm việc</h3>
                                    {applicant.experience?.map((exp, index) => (
                                        <div key={index} className="experience-item">
                                            <div className="exp-header">
                                                <h4>{exp.position}</h4>
                                                <span className="exp-company">{exp.company}</span>
                                            </div>
                                            <div className="exp-duration">{exp.duration}</div>
                                            {exp.description && (
                                                <div className="exp-description">{exp.description}</div>
                                            )}
                                        </div>
                                    ))}
                                </div>

                                <div className="education-section">
                                    <h3>Học vấn</h3>
                                    {applicant.education?.map((edu, index) => (
                                        <div key={index} className="education-item">
                                            <div className="edu-header">
                                                <h4>{edu.degree}</h4>
                                                <span className="edu-school">{edu.school}</span>
                                            </div>
                                            <div className="edu-field">{edu.field}</div>
                                            <div className="edu-year">{edu.year}</div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {activeTab === 'interviews' && (
                            <div className="interviews-tab">
                                <div className="interviews-list">
                                    {applicant.interviews?.map((interview, index) => (
                                        <div key={index} className="interview-item">
                                            <div className="interview-header">
                                                <span className="interview-type">
                                                    {interviewTypes.find(t => t.value === interview.type)?.icon}
                                                    {interviewTypes.find(t => t.value === interview.type)?.label}
                                                </span>
                                                <span 
                                                    className="interview-status"
                                                    style={{ 
                                                        backgroundColor: interviewStatuses.find(s => s.value === interview.status)?.color 
                                                    }}
                                                >
                                                    {interviewStatuses.find(s => s.value === interview.status)?.label}
                                                </span>
                                            </div>
                                            <div className="interview-details">
                                                <div>📅 {formatDate(interview.scheduledAt)}</div>
                                                <div>⏱️ {interview.duration} phút</div>
                                                {interview.location && <div>📍 {interview.location}</div>}
                                                {interview.meetingLink && <div>🔗 {interview.meetingLink}</div>}
                                            </div>
                                            {interview.feedback && (
                                                <div className="interview-feedback">
                                                    <strong>Phản hồi:</strong> {interview.feedback}
                                                </div>
                                            )}
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}

                        {activeTab === 'notes' && (
                            <div className="notes-tab">
                                <div className="add-note-section">
                                    <h3>Thêm ghi chú</h3>
                                    <textarea
                                        value={newNote}
                                        onChange={(e) => setNewNote(e.target.value)}
                                        placeholder="Nhập ghi chú..."
                                        rows="3"
                                    />
                                    <div className="note-options">
                                        <label>
                                            <input
                                                type="checkbox"
                                                checked={isPrivateNote}
                                                onChange={(e) => setIsPrivateNote(e.target.checked)}
                                            />
                                            Ghi chú riêng tư
                                        </label>
                                        <button 
                                            className="btn-primary"
                                            onClick={handleAddNote}
                                            disabled={!newNote.trim()}
                                        >
                                            Thêm ghi chú
                                        </button>
                                    </div>
                                </div>

                                <div className="notes-list">
                                    <h3>Ghi chú ({applicant.notes?.length || 0})</h3>
                                    {applicant.notes?.map((note, index) => (
                                        <div key={index} className="note-item">
                                            <div className="note-header">
                                                <span className="note-author">{note.author?.fullName}</span>
                                                <span className="note-date">{formatDate(note.date)}</span>
                                                {note.isPrivate && <span className="private-badge">Riêng tư</span>}
                                            </div>
                                            <div className="note-content">{note.content}</div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        )}
                    </div>
                </div>

                {/* Interview Form Modal */}
                {showInterviewForm && (
                    <div className="form-modal-overlay">
                        <div className="form-modal">
                            <h3>Lên lịch phỏng vấn</h3>
                            <div className="form-group">
                                <label>Loại phỏng vấn:</label>
                                <select 
                                    value={interviewForm.type}
                                    onChange={(e) => setInterviewForm({...interviewForm, type: e.target.value})}
                                >
                                    {interviewTypes.map(type => (
                                        <option key={type.value} value={type.value}>
                                            {type.icon} {type.label}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            <div className="form-group">
                                <label>Thời gian:</label>
                                <input
                                    type="datetime-local"
                                    value={interviewForm.scheduledAt}
                                    onChange={(e) => setInterviewForm({...interviewForm, scheduledAt: e.target.value})}
                                />
                            </div>
                            <div className="form-group">
                                <label>Thời lượng (phút):</label>
                                <input
                                    type="number"
                                    value={interviewForm.duration}
                                    onChange={(e) => setInterviewForm({...interviewForm, duration: parseInt(e.target.value)})}
                                />
                            </div>
                            <div className="form-group">
                                <label>Địa điểm:</label>
                                <input
                                    type="text"
                                    value={interviewForm.location}
                                    onChange={(e) => setInterviewForm({...interviewForm, location: e.target.value})}
                                />
                            </div>
                            <div className="form-group">
                                <label>Link meeting:</label>
                                <input
                                    type="url"
                                    value={interviewForm.meetingLink}
                                    onChange={(e) => setInterviewForm({...interviewForm, meetingLink: e.target.value})}
                                />
                            </div>
                            <div className="form-actions">
                                <button 
                                    className="btn-secondary"
                                    onClick={() => setShowInterviewForm(false)}
                                >
                                    Hủy
                                </button>
                                <button 
                                    className="btn-primary"
                                    onClick={handleScheduleInterview}
                                >
                                    Lên lịch
                                </button>
                            </div>
                        </div>
                    </div>
                )}

                {/* Offer Form Modal */}
                {showOfferForm && (
                    <div className="form-modal-overlay">
                        <div className="form-modal">
                            <h3>Tạo đề nghị làm việc</h3>
                            <div className="form-group">
                                <label>Mức lương:</label>
                                <div className="salary-inputs">
                                    <input
                                        type="number"
                                        placeholder="Số tiền"
                                        value={offerForm.salary.amount}
                                        onChange={(e) => setOfferForm({
                                            ...offerForm, 
                                            salary: {...offerForm.salary, amount: e.target.value}
                                        })}
                                    />
                                    <select 
                                        value={offerForm.salary.currency}
                                        onChange={(e) => setOfferForm({
                                            ...offerForm, 
                                            salary: {...offerForm.salary, currency: e.target.value}
                                        })}
                                    >
                                        <option value="VND">VND</option>
                                        <option value="USD">USD</option>
                                    </select>
                                    <select 
                                        value={offerForm.salary.period}
                                        onChange={(e) => setOfferForm({
                                            ...offerForm, 
                                            salary: {...offerForm.salary, period: e.target.value}
                                        })}
                                    >
                                        <option value="monthly">Tháng</option>
                                        <option value="yearly">Năm</option>
                                    </select>
                                </div>
                            </div>
                            <div className="form-group">
                                <label>Ngày bắt đầu:</label>
                                <input
                                    type="date"
                                    value={offerForm.startDate}
                                    onChange={(e) => setOfferForm({...offerForm, startDate: e.target.value})}
                                />
                            </div>
                            <div className="form-group">
                                <label>Hạn phản hồi:</label>
                                <input
                                    type="date"
                                    value={offerForm.expiresAt}
                                    onChange={(e) => setOfferForm({...offerForm, expiresAt: e.target.value})}
                                />
                            </div>
                            <div className="form-group">
                                <label>Ghi chú:</label>
                                <textarea
                                    value={offerForm.notes}
                                    onChange={(e) => setOfferForm({...offerForm, notes: e.target.value})}
                                    rows="3"
                                />
                            </div>
                            <div className="form-actions">
                                <button 
                                    className="btn-secondary"
                                    onClick={() => setShowOfferForm(false)}
                                >
                                    Hủy
                                </button>
                                <button 
                                    className="btn-primary"
                                    onClick={handleCreateOffer}
                                >
                                    Tạo đề nghị
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ApplicantDetailModal;
