        if (localError) setLocalError('');
        if (error) clearError();
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validation
        if (!signupData.fullName || !signupData.email || !signupData.password) {
            setLocalError('Vui lÃ²ng nháº­p Ä‘áº§y Ä‘á»§ thÃ´ng tin');
            return;
        }

        if (signupData.password !== signupData.confirmPassword) {
            setLocalError('Máº­t kháº©u xÃ¡c nháº­n khÃ´ng khá»›p');
            return;
        }

        if (signupData.password.length < 6) {
            setLocalError('Máº­t kháº©u pháº£i cÃ³ Ã­t nháº¥t 6 kÃ½ tá»±');
            return;
        }

        try {
            setIsLoading(true);
            setLocalError('');

            if (userType === 'company') {
                // Register company using register-company endpoint
                const companyData = {
                    name: signupData.fullName,
                    email: signupData.email,
                    password: signupData.password,
                    description: '', // You can add description field later
                    address: '' // You can add address field later
                };

                await registerCompany(companyData);
            } else {
                // Register job seeker using regular register endpoint
                const userData = {
                    fullName: signupData.fullName,
                    email: signupData.email,
                    password: signupData.password,
                    role: 'jobseeker'
                };

                await register(userData);
            }

            // Registration successful - show email verification
            info('Vui lÃ²ng kiá»ƒm tra email Ä‘á»ƒ xÃ¡c thá»±c tÃ i khoáº£n', 'ÄÄƒng kÃ½ thÃ nh cÃ´ng');
            setShowEmailVerification(true);
        } catch (error) {
            const errorMessage = error.message || 'ÄÄƒng kÃ½ tháº¥t báº¡i';
            setLocalError(errorMessage);
            showError(errorMessage, 'Lá»—i Ä‘Äƒng kÃ½');
        } finally {
            setIsLoading(false);
        }
    };

    const handleVerificationSuccess = (response) => {
        setShowEmailVerification(false);
        onClose();
        // Show success message
        success('ÄÄƒng kÃ½ thÃ nh cÃ´ng! ChÃ o má»«ng báº¡n Ä‘áº¿n vá»›i PartGO!', 'HoÃ n thÃ nh');
    };

    const handleCloseVerification = () => {
        setShowEmailVerification(false);
        onClose();
    };

    return (
        <div>
            {/* Email Verification Modal */}
            {shouldShowEmailVerification && (
                <EmailVerification
                    userId={pendingVerification.userId}
