                                                        }}
                                                        onClick={() => setUserType('jobseeker')}
                                                        disabled={isLoading}
                                                    >
                                                        Job Seeker
                                                    </button>
                                                    <button
                                                        type="button"
                                                        className="btn btn-sm px-4"
                                                        style={{
                                                            backgroundColor: userType === 'company' ? '#ff6b35' : 'transparent',
                                                            color: userType === 'company' ? 'white' : '#6c757d',
                                                            borderRadius: '20px',
                                                            border: 'none',
                                                            fontWeight: '500'
                                                        }}
                                                        onClick={() => setUserType('company')}
                                                        disabled={isLoading}
                                                    >
