                                            }}>
                                                <span style={{ fontSize: '18px', marginRight: '12px' }}>ðŸŒ</span>
                                                <span style={{ color: '#333', fontWeight: '500' }}>ÄÄƒng kÃ½ vá»›i Google</span>
                                            </button>

                                            <div className="text-center mb-3">
                                                <span className="text-muted">Hoáº·c Ä‘Äƒng kÃ½ báº±ng email</span>
                                            </div>

                                            <form onSubmit={handleSubmit}>
                                                {/* Error Message */}
                                                {(localError || error) && (
                                                    <div className="alert alert-danger mb-3" role="alert">
                                                        {localError || error}
                                                    </div>
                                                )}

                                                {/* Full Name */}
                                                <div className="mb-3">
                                                    <label className="form-label fw-medium" style={{ color: '#333' }}>
                                                        {userType === 'company' ? 'TÃªn cÃ´ng ty' : 'Há» vÃ  tÃªn'}
                                                    </label>
                                                    <input
                                                        type="text"
                                                        className="form-control"
                                                        name="fullName"
                                                        value={signupData.fullName}
                                                        onChange={handleInputChange}
                                                        placeholder={userType === 'company' ? 'Nháº­p tÃªn cÃ´ng ty' : 'Nháº­p há» vÃ  tÃªn cá»§a báº¡n'}
                                                        style={{
                                                            borderRadius: '8px',
