import React, { createContext, useContext, useState, useCallback } from 'react';
import Toast from './Toast';

const ToastContext = createContext();

export const useToast = () => {
    const context = useContext(ToastContext);
    if (!context) {
        throw new Error('useToast must be used within a ToastProvider');
    }
    return context;
};

export const ToastProvider = ({ children }) => {
    const [toasts, setToasts] = useState([]);

    const addToast = useCallback((toast) => {
        const id = Date.now() + Math.random();
        const newToast = {
            id,
            type: 'info',
            duration: 5000,
            ...toast,
        };

        setToasts(prev => [...prev, newToast]);
        return id;
    }, []);

    const removeToast = useCallback((id) => {
        setToasts(prev => prev.filter(toast => toast.id !== id));
    }, []);

    const success = useCallback((message, title = 'Thành công', options = {}) => {
        return addToast({
            type: 'success',
            title,
            message,
            ...options,
        });
    }, [addToast]);

    const error = useCallback((message, title = 'Lỗi', options = {}) => {
        return addToast({
            type: 'error',
            title,
            message,
            duration: 7000, // Error messages stay longer
            ...options,
        });
    }, [addToast]);

    const warning = useCallback((message, title = 'Cảnh báo', options = {}) => {
        return addToast({
            type: 'warning',
            title,
            message,
            duration: 6000,
            ...options,
        });
    }, [addToast]);

    const info = useCallback((message, title = 'Thông tin', options = {}) => {
        return addToast({
            type: 'info',
            title,
            message,
            ...options,
        });
    }, [addToast]);

    const clearAll = useCallback(() => {
        setToasts([]);
    }, []);

    const value = {
        addToast,
        removeToast,
        success,
        error,
        warning,
        info,
        clearAll,
    };

    return (
        <ToastContext.Provider value={value}>
            {children}
            <div className="toast-container">
                {toasts.map(toast => (
                    <Toast
                        key={toast.id}
                        {...toast}
                        onClose={removeToast}
                    />
                ))}
            </div>
        </ToastContext.Provider>
    );
};
